import React from 'react';
import { useHistory } from 'react-router-dom';

function Register ({ handleRegister }) {
    const [data, setData] = React.useState({
        email: '',
        password: ''
    });

    const [message, setMessage] = React.useState('');

    const history = useHistory();

    function handleChangeEmail (e) {
        setEmail(e.target.value)
    }

    function handleChangePassword (e) {
        setPassword(e.target.value)
    }

    function handleSubmit (e) {
        e.preventDefault();
    }

    return(
        <div className="auth">
            <div className="auth__container">
                <form className="auth__form"
                      name="logForm"
                      onSubmit={handleSubmit}
                >
                    <h2 className="auth__title">Регистрация</h2>
                    <input className="auth__input"
                           id="user-email"
                           required
                           minLength="2"
                           maxLength="40"
                           type="text"
                           placeholder="email"
                           name="regEmail"
                           value={email || ''}
                           onChange={handleChangeEmail} />
                    <span className="error" id="user-email-error"></span>
                    <input className="auth__input"
                           id="user-password"
                           required
                           minLength="2"
                           maxLength="40"
                           type="text"
                           placeholder="Пароль"
                           name="regPassword"
                           value={password || ''}
                           onChange={handleChangePassword} />
                    <span className="error" id="user-password-error"></span>
                    <button className="auth__submit" type="submit">Зарегистрироваться</button>
                    <h2 className="auth__subtitle">Уже зарегистрированы? Войти</h2>
                </form>
            </div>
        </div>
    )
}

export default Register;
