import React from 'react';
import Api from '../utils/Api';
import { api} from '../utils/Api';

function Main({ onEditAvatar, onEditProfile, onAddPlace }) {
    const [userName, setUserName] = React.useState("");
    const [userDescription, setUserDescription] = React.useState("");
    const [userAvatar, setUserAvatar] = React.useState("");
    const [cards, setCards] = React.useState([]);
    React.useEffect(() => {
        api.getUserApi(userName, userDescription, userAvatar)
            .then(res => {
                setUserName(res.name)
                setUserDescription(res.about)
                setUserAvatar(res.avatar)
            })
    }, [])

    React.useEffect(() => {
        api.getAllCards(cards)
            .then(data => {
                setCards(data)
                // console.log(data)
                // setCards(data.map((item) => ({
                //     id: item._id,
                //     image: item.link,
                //     name: item.name,
                //    likes: item.likes
                // })))

            })
    }, []);

    return(
            <main className="content">
                <section className="profile">
                    <div className="profile__info">
                        <div className="profile__avatar">
                            <img style={{
                                backgroundImage: `url(${userAvatar})`
                            }}
                                 className="profile__avatar-image" src="" alt="Аватарка"  />
                            <a className="profile__overlay" href="#">
                                <button className="profile__edit-avatar"
                                onClick={onEditAvatar}>
                                </button>
                            </a>
                        </div>
                        <div className="profile__text">
                            <div className="profile__block">
                                <h1 className="profile__info-name">{userName}</h1>
                                <button className="profile__edit-button"
                                onClick={onEditProfile}>
                                </button>
                            </div>
                            <p className="profile__info-text">{userDescription}</p>
                        </div>
                    </div>
                    <button className="profile__add-button"
                    onClick={onAddPlace}>
                    </button>
                </section>

                {/*/!*Попап изменения аватара*!/*/}
                {/*<div className="popup popup__avatar-edit">*/}
                {/*    <div className="popup__container  popup__container-avatar-edit">*/}
                {/*        <form className="popup__form popup__form-avatar-edit">*/}
                {/*            <button className="popup__close popup__close-avatar-edit" type="button"></button>*/}
                {/*            <h2 className="popup__title">Обновить аватар</h2>*/}
                {/*            <input type="url" className="popup__input popup__input_avatar-edit" id="link-avatar" required*/}
                {/*                   placeholder="Ссылка на картинку"*/}
                {/*                   name="link" value="" />*/}
                {/*            <span className="error" id="link-avatar-error"></span>*/}
                {/*            <button className="popup__submit popup__submit-avatar-edit"*/}
                {/*                    type="submit">Сохранить*/}
                {/*            </button>*/}
                {/*        </form>*/}
                {/*    </div>*/}
                {/*</div>*/}

                {/*Попап изменения информации*/}
                {/*<div className="popup popup__info">*/}
                {/*    <div className="popup__container popup__container-min">*/}
                {/*        <form className="popup__form popup__form-min popup__form-info" name="information" noValidate >*/}
                {/*            <button className="popup__close popup__close-info" type="button"></button>*/}
                {/*            <h2 className="popup__title">Редактировать профиль</h2>*/}
                {/*            <input className="popup__input popup__input_type_firstname" id="user-name" required minLength="2"*/}
                {/*                   maxLength="40"*/}
                {/*                   type="text" placeholder="Имя" name="firstname" value="" />*/}
                {/*            <span className="error" id="user-name-error"></span>*/}
                {/*            <input className="popup__input popup__input_type_career" id="user-career" required minLength="2"*/}
                {/*                   maxLength="200"*/}
                {/*                   type="text" placeholder="Работа" name="career" value="" />*/}
                {/*            <span className="error" id="user-career-error"></span>*/}
                {/*            <button className="popup__submit popup__submit-min" type="submit">Сохранить</button>*/}
                {/*        </form>*/}
                {/*    </div>*/}
                {/*</div>*/}

                {/*/!*Попап добавления картинок*!/*/}
                {/*<div className="popup popup__add-image">*/}
                {/*    <div className="popup__container">*/}
                {/*        <form className="popup__form popup__form_add-image" name="information" noValidate>*/}
                {/*            <button className="popup__close popup__close-add-image" type="button"></button>*/}
                {/*            <h2 className="popup__title popup__title-min">Новое место</h2>*/}
                {/*            <input className="popup__input popup__input_type_name" id="name-card" required minLength="2"*/}
                {/*                   maxLength="30"*/}
                {/*                   type="text" placeholder="Название" name="name" value="" />*/}
                {/*            <span className="error" id="name-card-error"></span>*/}
                {/*            <input type="url" className="popup__input popup__input_type_link" id="link" required*/}
                {/*                   placeholder="Ссылка на картинку"*/}
                {/*                   name="link" value="" />*/}
                {/*            <span className="error" id="link-error"></span>*/}
                {/*            <button className="popup__submit popup__save-image-button"*/}
                {/*                    type="submit">Создать*/}
                {/*            </button>*/}
                {/*        </form>*/}
                {/*    </div>*/}
                {/*</div>*/}

                {/*/!* Попап подтверждения удаления  *!/*/}
                {/*<div className="popup popup__with-submit">*/}
                {/*    <div className="popup__container popup__container-with-submit">*/}
                {/*        <form className="popup__form popup__form-with-submit">*/}
                {/*            <button className="popup__close popup__close-with-submit" type="button"></button>*/}
                {/*            <h2 className="popup__title popup__title-with-submit">Вы уверены?</h2>*/}
                {/*            <button className="popup__submit popup__submit-with-submit" type="submit"></button>*/}
                {/*        </form>*/}
                {/*    </div>*/}
                {/*</div>*/}

                {/*Попап открытия изображений*/}
                {/*<div className="popup popup__open-card">*/}
                {/*    <div className="popup__card">*/}
                {/*        <button className="popup__close popup__close-card" type="button"></button>*/}
                {/*        <img className="popup__image" src="" alt="Просмотр фото" />*/}
                {/*        <h2 className="popup__text"></h2>*/}
                {/*    </div>*/}
                {/*</div>*/}

                <section className="elements">
                    {cards.map((card) => {
                        return <li className="card">
                            <img style={{
                                backgroundImage: `url(${userAvatar})`
                            }} className="card__image" src="" alt="Фото" />
                            <button className="card__del"></button>
                            <div className="card__items">
                                <h2 className="card__title">{cards}</h2>
                                <div className="card__item">
                                    <button className="card__button"></button>
                                    <p className="card__number-likes"></p>
                                </div>
                            </div>
                        </li>
                    })}

                </section>
            </main>
    )


}

export default Main;
