function ImagePopup({isOpen, onClose, name, type, link, text, data}) {
    return (
        <div className={isOpen ? `popup popup__${name} popup_is-opened` : `popup popup__${name}`}>
            <div className="popup__card">
                <button className={`popup__close popup__close-${type}`} type="button"
                    onClick={onClose}>
                </button>
                <img className="popup__image" src={data.link} alt={data.text} />
                <h2 className="popup__text">{data.text}</h2>
            </div>
        </div>
    )
}

export default ImagePopup;
