import React from 'react';
import { api } from '../utils/Api';
import Card from './Card';

function Main({ onEditAvatar, onEditProfile, onAddPlace }) {
    const [userName, setUserName] = React.useState("");
    const [userDescription, setUserDescription] = React.useState("");
    const [userAvatar, setUserAvatar] = React.useState("");
    const [cards, setCards] = React.useState([]);
    const [isLoading, setIsLoading] = React.useState(false)

    React.useEffect(() => {
        api.getUserApi(userName, userDescription, userAvatar)
            .then(res => {
                setUserName(res.name)
                setUserDescription(res.about)
                setUserAvatar(res.avatar)
            })
    }, [])

    React.useEffect(() => {
        setIsLoading9
        api.getAllCards(cards)
            .then(data => {
                setCards(data)
            })
    }, []);

    return(
            <main className="content">
                <section className="profile">
                    <div className="profile__info">
                        <div className="profile__avatar">
                            <img className="profile__avatar-image" src={userAvatar} alt="Аватарка"  />
                            <a className="profile__overlay" href="#">
                                <button className="profile__edit-avatar"
                                onClick={onEditAvatar}>
                                </button>
                            </a>
                        </div>
                        <div className="profile__text">
                            <div className="profile__block">
                                <h1 className="profile__info-name">{userName}</h1>
                                <button className="profile__edit-button"
                                onClick={onEditProfile}>
                                </button>
                            </div>
                            <p className="profile__info-text">{userDescription}</p>
                        </div>
                    </div>
                    <button className="profile__add-button"
                    onClick={onAddPlace}>
                    </button>
                </section>

                <section className="elements">
                    {
                        cards.map(({id, ...props}) => <Card key={id} {...props} />)
                    }
                </section>
            </main>
    )


}

export default Main;
