import '../index.css';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import React from 'react';

function App() {
    const [ isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
    const [ isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
    const [ isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);


    function handleEditAvatarClick () {
        setEditAvatarPopupOpen(true);
    }

    function handleEditProfileClick () {
        setEditProfilePopupOpen(true);
    }

    function handleAddPlaceClick () {
        setAddPlacePopupOpen(true);
    }


  return (
    <div className="App">
        <body className="page">
            <Header />
            <Main
                onEditAvatar={handleEditAvatarClick}
                onEditProfile={handleEditProfileClick}
                onAddPlace={handleAddPlaceClick}
            />
            <Footer />
            <PopupWithForm
                name="popup__avatar-edit"
                container="popup__container-avatar-edit"
                title="Обновить аватар"
                submit="popup__submit-avatar-edit"
                is
            />
            <ImagePopup />

            {/* template разметка карточек */}
             <template className="template">
            <li className="card">
                <img className="card__image" src="" alt="Фото" />
                    <button className="card__del"></button>
                    <div className="card__items">
                        <h2 className="card__title"></h2>
                        <div className="card__item">
                            <button className="card__button"></button>
                            <p className="card__number-likes"></p>
                        </div>

                    </div>
            </li>
        </template>

        </body>
    </div>
  );
}

export default App;
