import React from 'react';
import { api } from '../utils/Api';
import Card from './Card';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Main({ onEditAvatar, onEditProfile, onAddPlace,  handleCardClick}) {
    const [cards, setCards] = React.useState([]);

    const currentUser = React.useContext(CurrentUserContext);

    React.useEffect(() => {
        api.getAllCards()
            .then(data => {
                setCards(data)
            }).catch((err) => alert(err));
    }, []);

    // Лайк
    function handleCardLike(card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        isLiked ? (
            //Отправляем запрос для удаления лайка
            api.dislikeCard(card._id).then((res) => {
                console.log(res)
                const dislikeCard = cards.map((item) => item._id === card._id ? res : item);
                setCards(dislikeCard)
            })
            ) : (
            // Отправляем запрос в API и получаем обновлённые данные карточки
            api.likeCard(card._id, !isLiked).then((newCard) => {
                console.log(newCard)
                // Формируем новый массив на основе имеющегося, подставляя в него новую карточку
                const newCards = cards.map((c) => c._id === card._id ? newCard : c);
                // Обновляем стейт
                setCards(newCards);
            })
        )
    // Удаление карточки


    }

    return(
        <main className="content">
            <section className="profile">
                <div className="profile__info">
                    <div className="profile__avatar">
                        <img className="profile__avatar-image" src={currentUser.avatar} alt="Аватарка"  />
                        <div className="profile__overlay"
                             onClick={onEditAvatar}>
                            <button className="profile__edit-avatar"></button>
                        </div>
                    </div>
                    <div className="profile__text">
                        <div className="profile__block">
                            <h1 className="profile__info-name">{currentUser.name}</h1>
                            <button className="profile__edit-button"
                                    onClick={onEditProfile}>
                            </button>
                        </div>
                        <p className="profile__info-text">{currentUser.about}</p>
                    </div>
                </div>
                <button className="profile__add-button"
                        onClick={onAddPlace}>
                </button>
            </section>
            <section className="elements">
                {
                    cards.map((card) => <Card key={card._id} card={card} onCardClick={handleCardClick} onCardLike={handleCardLike} />)
                }
            </section>
        </main>
    )

}

export default Main;
